import { test, expect } from '@playwright/test';

test('ExtractLatestNewsTitles', async ({ page }) => {
  // Step 1: Navigate to Cricbuzz homepage
  await page.goto('https://www.cricbuzz.com/', { waitUntil: 'networkidle' });

  // Step 2: Locate the section containing latest cricket news articles
  const newsSection = page.locator('h2:has-text("LATEST NEWS")').locator('..');

  // Step 3: Extract the titles of the articles
  const articleTitles = await newsSection.locator('a').allTextContents();
  console.log('Latest News Titles:', articleTitles);

  // Step 4: Close the browser
  await page.close();
});
