import { test, expect } from '@playwright/test';

test('search_alchemist_google', async ({ page }) => {
  // Step 1: Navigate to Google.com
  await page.goto('https://www.google.com', { waitUntil: 'networkidle' });

  // Step 2: Search for the book named 'alchemist'
  const searchBox = page.getByRole('combobox', { name: 'Search' });
  await searchBox.fill('alchemist');
  await searchBox.press('Enter');

  // Step 3: Verify the book name 'The Alchemist' is visible in the search results
  const bookName = page.getByText('The Alchemist');
  await expect(bookName).toBeVisible({ timeout: 10000 });

  // Step 4: Close the browser
  await page.close();
});
