import { test, expect } from '@playwright/test';

test('Navigate to Azure AI Foundry and check Model Catalogue', async ({ page }) => {
  // Navigate to Azure AI Foundry
  await page.goto('https://ai.azure.com', { waitUntil: 'networkidle' });

  // Verify page title
  await expect(page).toHaveTitle(/Azure AI Foundry/);

  // Click on "Go to full model catalog"
  const modelCatalogLink = page.getByRole('link', { name: 'Go to full model catalog' });
  await expect(modelCatalogLink).toBeVisible({ timeout: 10000 });
  await modelCatalogLink.click();

  // Verify navigation to Model Catalogue page
  await expect(page).toHaveURL(/\/explore\/models/);
  await expect(page).toHaveTitle(/Model catalog - Azure AI Foundry/);

  // Close the browser
  await page.close();
});
